pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";


/**
 * @dev
 *
 * Terms contract, contains a string with a link to the product terms & considtions.
 * 
 * The contract contains one role:
 *  - An owner, which can set the terms
 *
 */
contract Terms is OwnableUpgradeable {

    string public terms;

    event NewTerms(string newTerms);

    function setTerms(string memory newTerms) external onlyOwner {
        _setTerms(newTerms);
    }

    function _setTerms(string memory newTerms) internal {
        terms = newTerms;
        emit NewTerms(newTerms);
    }

}"
