// File contracts/FlashLoanLiquidator.sol

pragma solidity ^0.8.13;




contract FlashLoanLiquidator is LiquidatorBase, IFlashLoanReceiver {
  using SafeERC20 for IERC20;

  struct LiquidateERC20Params {
    address collateralAsset;
    address debtAsset;
    address user;
    uint256 debtToCover;
    address[] swapPath;
    uint8 mode;
    address flashLoanAsset;
    address[] flashLoanToDebtSwapPath; // flashLoanAsset -> debtAsset
  }

  constructor(LiquidatorConfig config) LiquidatorBase(config) {}

  function liquidateERC20(LiquidateERC20Params memory params)
    public
    onlyExecutor
  {
    address receiverAddress = address(this);

    // the various assets to be flashed
    address[] memory assets = new address[](1);
    assets[0] = params.flashLoanAsset;

    // the amount to be flashed for each asset
    uint256[] memory amounts = new uint256[](1);

    if (params.mode == 0) {
      amounts[0] = params.debtToCover;
    } else {
      uint256[] memory _amounts = CONFIG._uniswapV2Router().getAmountsIn(
        params.debtToCover,
        params.flashLoanToDebtSwapPath
      );
      amounts[0] = _amounts[0];
    }

    console.log("amounts[0]");

    // 0 = no debt, 1 = stable, 2 = variable
    uint256[] memory modes = new uint256[](1);
    modes[0] = 0;

    address onBehalfOf = address(this);

    bytes memory paramsBytes = abi.encode(params);

    uint16 referralCode = 0;

    // 1. lend money
    // 2. call callback
    // 3. repay money
    CONFIG._flashLoanPool().flashLoan(
      receiverAddress,
      assets,
      amounts,
      modes,
      onBehalfOf,
      paramsBytes,
      referralCode
    );
  }

  function executeOperation(
    address[] calldata assets,
    uint256[] calldata amounts,
    uint256[] calldata premiums,
    address initiator,
    bytes calldata params_
  ) external returns (bool) {
    initiator;
    LiquidateERC20Params memory params = abi.decode(
      params_,
      (LiquidateERC20Params)
    );

    if (params.mode != 0) {
      _safeApprove(
        params.flashLoanAsset,
        address(CONFIG._uniswapV2Router()),
        amounts[0]
      );

      CONFIG._uniswapV2Router().swapTokensForExactTokens(
        params.debtToCover,
        amounts[0],
        params.flashLoanToDebtSwapPath,
        address(this),
        block.timestamp
      );
    }

    console.log("swap 1");

    // liquidate ERC20
    _safeApprove(params.debtAsset, address(CONFIG._pool()), params.debtToCover);

    console.log("approve 1");

    CONFIG._pool().liquidationCall(
      params.collateralAsset,
      params.debtAsset,
      params.user,
      params.debtToCover,
      false
    );

    console.log("liquidationCall");

    //
    uint256 amountOwing = amounts[0] + premiums[0];
    uint256 transferToOwner;

    if (params.collateralAsset != params.flashLoanAsset) {
      _safeApprove(
        params.collateralAsset,
        address(CONFIG._uniswapV2Router()),
        IERC20(params.collateralAsset).balanceOf(address(this))
      );
      console.log("approve 2");

      uint256 amountNeeded = amountOwing -
        IERC20(params.flashLoanAsset).balanceOf(address(this));

      CONFIG._uniswapV2Router().swapTokensForExactTokens(
        amountNeeded,
        IERC20(params.collateralAsset).balanceOf(address(this)),
        params.swapPath,
        address(this),
        block.timestamp
      );

      console.log("swap 2");

      transferToOwner = IERC20(params.collateralAsset).balanceOf(address(this));
    } else {
      transferToOwner =
        IERC20(params.collateralAsset).balanceOf(address(this)) -
        amountOwing;
    }

    IERC20(params.collateralAsset).safeTransfer(owner(), transferToOwner);

    console.log("transfer To Owner");

    if (params.mode != 0) {
      IERC20(params.debtAsset).safeTransfer(
        owner(),
        IERC20(params.debtAsset).balanceOf(address(this))
      );
      console.log("transfer left debtAsset");
    }

    // Approve the LendingPool contract allowance to *pull* the owed amount
    _safeApprove(
      params.flashLoanAsset,
      address(CONFIG._flashLoanPool()),
      amountOwing
    );
    console.log("approve 3");

    return true;
  }

  function _safeApprove(
    address _token,
    address _spender,
    uint256 _amount
  ) internal {
    IERC20(_token).safeApprove(_spender, 0);
    IERC20(_token).safeApprove(_spender, _amount);
  }
}
