// File: contracts/SpermNFT.sol


pragma solidity >=0.7.0 <0.9.0;






contract SpermNFT is ERC721Enumerable, Ownable, ERC721Royalty {
    using Strings for uint256;
    using SafeMath for uint256;
    using ECDSA for bytes32;

    string public baseURI;
    string private _baseExtension = ".json";
    string private _notRevealedUri;
    string private _contractURI;
    uint256 public cost = 0 ether; //price for 1 nft
    uint256 public maxSupply = 1000; // number of nft in the collection
    uint256 public maxMintAmount = 1;
    uint256 public nftPerAddressLimit = 1;
    bool public paused = false;
    bool public revealed = false;
    bool public onlyWhitelisted = true;
    mapping(address => uint256) public addressMintedBalance;
    uint8 private _og = 2;
    uint8 private _wl = 1;

    constructor() ERC721("SpermNFT", "SPERM") {
        setBaseURI("ipfs://QmWagEWb2WA6HVuJrYgu64Jc5mFbWczvBiDRhuJE279Fkb/");
        setNotRevealedURI("ipfs://Qmcsk5Ex362ok7wpFxNgsdyWNZZN1fPiiohftDn9FchuUR");
        _setDefaultRoyalty(owner(), 1000);
        setContractURI("ipfs://QmNie8dNLnzGhC9K6aGWR3j5fnHLJGD4mT21BhwDx2vk4X");
    }

    function contractURI() public view returns (string memory) {
        return _contractURI;
    }

    function setContractURI(string memory _URI) public onlyOwner {
        _contractURI = _URI;
    }

    // internal
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721Enumerable, ERC721Royalty)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function _burn(uint256 tokenId)
        internal
        virtual
        override(ERC721, ERC721Royalty)
    {
        super._burn(tokenId);
        _resetTokenRoyalty(tokenId);
    }

    function setMaxSupply(uint256 _amount) public onlyOwner {
        maxSupply = _amount;
    }

    function mint(uint256 _mintAmount, bytes memory _signature) public payable {
        require(!paused, "the contract is paused");
        uint256 supply = totalSupply();
        require(_mintAmount > 0, "need to mint at least 1 NFT");
        require(supply + _mintAmount <= maxSupply, "max NFT limit exceeded");
        uint256 maxMint = maxMintAmount;
        uint256 nftPerAdress = nftPerAddressLimit;

        if (msg.sender != owner()) {
            if (onlyWhitelisted == true) {
                if (!isOGListed(_signature)) {
                    require(
                        isWhitelisted(_signature),
                        "user is not whitelisted"
                    );
                } else {
                    maxMint = 2;
                    nftPerAdress = 2;
                }

                uint256 ownerMintedCount = addressMintedBalance[msg.sender];
                require(
                    ownerMintedCount + _mintAmount <= nftPerAdress,
                    "max NFT per address exceeded"
                );
            }

            require(
                _mintAmount <= maxMint,
                "max mint amount per session exceeded"
            );
            require(msg.value >= cost * _mintAmount, "insufficient funds!");
        }

        for (uint256 i = 1; i <= _mintAmount; i++) {
            addressMintedBalance[msg.sender]++;
            _safeMint(msg.sender, supply + i);
        }
    }

    function isMessageValid(bytes memory _signature, uint8 _nonce)
        public
        view
        returns (address, bool)
    {
        bytes32 messagehash = keccak256(
            abi.encodePacked(address(this), msg.sender, _nonce)
        );
        address signer = messagehash.toEthSignedMessageHash().recover(
            _signature
        );

        if (msg.sender == signer) {
            return (signer, true);
        } else {
            return (signer, false);
        }
    }

    function isWhitelisted(bytes memory _signature) public view returns (bool) {
        (, bool success) = isMessageValid(_signature, _wl);
        return success;
    }

    function isOGListed(bytes memory _signature) private view returns (bool) {
        (, bool success) = isMessageValid(_signature, _og);
        return success;
    }

    function walletOfOwner(address _owner)
        public
        view
        returns (uint256[] memory)
    {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }

    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        if (revealed == false) {
            return _notRevealedUri;
        }

        string memory currentBaseURI = _baseURI();
        return
            bytes(currentBaseURI).length > 0
                ? string(
                    abi.encodePacked(
                        currentBaseURI,
                        tokenId.toString(),
                        _baseExtension
                    )
                )
                : "";
    }

    //only owner
    function reveal() public onlyOwner {
        revealed = true;
    }

    function setNftPerAddressLimit(uint256 _limit) public onlyOwner {
        nftPerAddressLimit = _limit;
    }

    function setCost(uint256 _newCost) public onlyOwner {
        cost = _newCost;
    }

    function setmaxMintAmount(uint256 _newmaxMintAmount) public onlyOwner {
        maxMintAmount = _newmaxMintAmount;
    }

    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    function setBaseExtension(string memory _newBaseExtension)
        public
        onlyOwner
    {
        _baseExtension = _newBaseExtension;
    }

    function setNotRevealedURI(string memory _notRevealedURI) public onlyOwner {
        _notRevealedUri = _notRevealedURI;
    }

    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function setOnlyWhitelisted(bool _state) public onlyOwner {
        onlyWhitelisted = _state;
        if (onlyWhitelisted) maxMintAmount = 1;
        else maxMintAmount = 10;
    }

    function withdraw() external onlyOwner {
        (bool os, ) = payable(owner()).call{value: address(this).balance}("");
        require(os);
    }

    function getBalance() public view onlyOwner returns (uint256) {
        return address(this).balance;
    }
}
