contract UnamanoRegister is Ownable,ReentrancyGuard {

    using Address for address payable;

    struct UserInfo {
        uint8 f;
        address candyToken;
        uint256 candySupply;
        bytes32 authCodeHash;
    }
    address payable public operatorAddress;
    mapping(address => UserInfo) private registers;
    mapping(bytes32 => uint8) public authorizationCodes;

    event ProjectrRgister(address projectCreator);
    event ProjectrApprove(address projectCreator);
    event NewOperatorAddress(address operator);

    modifier onlyOperator() {
        require(msg.sender == operatorAddress, "Not operator");
        _;
    }

    constructor(
        address payable _operatorAddress
    ) {
        operatorAddress = _operatorAddress;
    }

    uint256 public payments = 10 ** 16;

    // Project creator submits for review
    function register() external payable nonReentrant {
        UserInfo storage userInfo = registers[msg.sender];
        require(userInfo.f == 0, "Repeat registration.");
        require(msg.value == payments, "Need Project registration fee: 0.01 eth");
        userInfo.f = 1;
        operatorAddress.sendValue(msg.value);
        emit ProjectrRgister(msg.sender);
    }

    // Operator Audit Project
    function approveProject(address _user, address _candyToken, uint256 _candySupply, string memory _authorizationCode) external nonReentrant onlyOperator{
        UserInfo storage userInfo = registers[_user];
        require(userInfo.f == 1, "No registration.");
        bytes32 codeHash = keccak256(bytes(_authorizationCode));
        // Check that the authorization code is unique
        require(authorizationCodes[codeHash] == 0, "Repeat authorizationCode.");
        userInfo.candyToken = _candyToken;
        userInfo.candySupply = _candySupply;
        userInfo.authCodeHash = codeHash;
        authorizationCodes[codeHash] = 1;
        emit ProjectrApprove(_user);
    }

    // change operator
    function setOperator(address payable _operatorAddress) external onlyOwner {
        require(_operatorAddress != address(0), "Cannot be zero address");
        operatorAddress = _operatorAddress;
        emit NewOperatorAddress(_operatorAddress);
    }

    function getUserInfo(address _user) external view returns (uint8 f, address _candyToken, uint256 _candySupply, bytes32 _authCodeHash) {
        UserInfo storage userInfo = registers[_user];
        if(userInfo.f == 0) {
            return (0, address(0), 0, bytes32(0));
        }
        return (userInfo.f, userInfo.candyToken, userInfo.candySupply, userInfo.authCodeHash);
    }

}
